    public int CountItem(int type, int stopCountingAt = 0)
    {
      int num = 0;
      for (int index = 0; index != 58; ++index)
      {
        if (this.inventory[index].stack > 0 && this.inventory[index].type == type)
        {
          num += this.inventory[index].stack;
          if (num >= stopCountingAt)
            return num;
        }
      }
      return num;
    }

    public bool ConsumeItem(int type, bool reverseOrder = false, bool includeVoidBag = false)
    {
      int num1 = 0;
      int num2 = 58;
      int num3 = 1;
      if (reverseOrder)
      {
        num1 = 57;
        num2 = -1;
        num3 = -1;
      }
      for (int index = num1; index != num2; index += num3)
      {
        if (this.inventory[index].stack > 0 && this.inventory[index].type == type)
        {
          --this.inventory[index].stack;
          if (this.inventory[index].stack <= 0)
            this.inventory[index].SetDefaults();
          return true;
        }
      }
      if (!includeVoidBag || !this.useVoidBag())
        return false;
      int index1 = this.FindItem(type, this.bank4.item);
      if (index1 == -1)
        return false;
      Item obj = this.bank4.item[index1];
      --obj.stack;
      if (obj.stack <= 0)
        obj.TurnToAir();
      return true;
    }

