          else
            ++num1;
        }
        if (this.nearbyTorch[8] && this.ZoneCorrupt)
          ++num2;
        if (this.nearbyTorch[19])
        {
          if (this.ZoneCrimson)
            ++num2;
          else
            ++num1;
        }
        if (this.nearbyTorch[11] && this.ZoneCrimson)
          ++num2;
        if (this.nearbyTorch[20])
        {
          if (this.ZoneHallow)
