    private void UpdateLadyBugLuckTime()
    {
      if (this.ladyBugLuckTimeLeft > 0)
      {
        this.ladyBugLuckTimeLeft -= Main.dayRate;
        if (this.ladyBugLuckTimeLeft >= 0)
          return;
        this.ladyBugLuckTimeLeft = 0;
      }
      else
      {
        if (this.ladyBugLuckTimeLeft >= 0)
          return;
        this.ladyBugLuckTimeLeft += Main.dayRate;
        if (this.ladyBugLuckTimeLeft <= 0)
          return;
        this.ladyBugLuckTimeLeft = 0;
      }
    }

    public void UpdateImmunity()
    {
      if (this.immune)
      {
        --this.immuneTime;
        if (this.immuneTime <= 0)
        {
          this.immune = false;
          this.immuneNoBlink = false;
        }
        if (this.immuneNoBlink)
        {
          this.immuneAlpha = 0;
        }
        else
        {
          this.immuneAlpha += this.immuneAlphaDirection * 50;
          if (this.immuneAlpha <= 50)
            this.immuneAlphaDirection = 1;
          else if (this.immuneAlpha >= 205)
            this.immuneAlphaDirection = -1;
        }
      }
      else
        this.immuneAlpha = 0;
      for (int index = 0; index < this.hurtCooldowns.Length; ++index)
      {
        if (this.hurtCooldowns[index] > 0)
          --this.hurtCooldowns[index];
      }
    }

    private void TryToPoop()
    {
      if (this.whoAmI != Main.myPlayer || !this.wellFed)
        return;
      int maxValue = 600;
      if (Main.rand.Next(maxValue) != 0)
        return;
      int min = 3;
      int buffIndex = this.FindBuffIndex(207);
      if (buffIndex == -1)
      {
        min = 2;
        buffIndex = this.FindBuffIndex(206);
      }
      if (buffIndex == -1)
      {
        min = 1;
        buffIndex = this.FindBuffIndex(26);
      }
      if (buffIndex == -1)
        return;
      int num = this.buffTime[buffIndex];
      this.DelBuff(buffIndex);
      int Stack = Utils.Clamp<int>(num / 3600 * min, min, 999);
      Vector2 mountedCenter = this.MountedCenter;
      ParticleOrchestrator.RequestParticleSpawn(false, ParticleOrchestraType.Digestion, new ParticleOrchestraSettings()
      {
        PositionInWorld = mountedCenter,
        MovementVector = new Vector2((float) -this.direction, 0.0f)
      }, new int?(this.whoAmI));
      int number = Item.NewItem(this.GetItemSource_Misc(9), mountedCenter, Vector2.Zero, 5395, Stack, noGrabDelay: true);
      if (Main.netMode == 0)
        Main.item[number].noGrabDelay = 100;
      if (Main.netMode != 1)
        return;
      NetMessage.SendData(21, number: number);
    }

    public void UpdateLifeRegen()
    {
      bool flag = false;
      if (this.shinyStone && this.IsStandingStillForSpecialEffects && this.itemAnimation == 0)
        flag = true;
      if (this.poisoned)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 4;
      }
      if (this.venom)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 30;
      }
      if (this.onFire)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 8;
      }
      if (this.onFire3)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 8;
      }
      if (this.onFrostBurn)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 16;
      }
      if (this.onFrostBurn2)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 16;
      }
      if (this.onFire2)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 24;
      }
      if (this.burned)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 60;
        this.moveSpeed *= 0.5f;
      }
      if (this.suffocating)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 40;
      }
      if (this.electrified)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 8;
        if (this.controlLeft || this.controlRight)
          this.lifeRegen -= 32;
      }
      if (this.tongued && Main.expertMode)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        this.lifeRegenTime = 0.0f;
        this.lifeRegen -= 100;
      }
      if (this.honey && this.lifeRegen < 0)
      {
        this.lifeRegen += 4;
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
      }
      if (this.lifeRegen < 0 && this.nebulaLevelLife > 0)
        this.lifeRegen = 0;
      if (flag && this.lifeRegen < 0)
        this.lifeRegen /= 2;
      ++this.lifeRegenTime;
      if (this.usedAegisCrystal)
        this.lifeRegenTime += 0.2f;
      if (this.crimsonRegen)
        ++this.lifeRegenTime;
      if (this.soulDrain > 0)
        this.lifeRegenTime += 2f;
      if (flag)
      {
        if ((double) this.lifeRegenTime > 90.0 && (double) this.lifeRegenTime < 1800.0)
          this.lifeRegenTime = 1800f;
        this.lifeRegenTime += 4f;
        this.lifeRegen += 4;
      }
      if (this.honey)
      {
        this.lifeRegenTime += 2f;
        this.lifeRegen += 2;
      }
      if (this.starving)
      {
        if (this.lifeRegen > 0)
          this.lifeRegen = 0;
        if (this.lifeRegenCount > 0)
          this.lifeRegenCount = 0;
        if ((double) this.lifeRegenTime > 0.0)
          this.lifeRegenTime = 0.0f;
        int num = 120 * this.statLifeMax2 / 3000;
        if (num < 4)
          num = 4;
        this.lifeRegen = -num;
      }
      if (this.soulDrain > 0)
      {
        int num = (5 + this.soulDrain) / 2;
        this.lifeRegenTime += (float) num;
        this.lifeRegen += num;
      }
      if (this.heartyMeal)
        this.lifeRegen += 3 * 120 / 60;
      if (this.whoAmI == Main.myPlayer && Main.SceneMetrics.HasCampfire)
        ++this.lifeRegen;
      if (this.whoAmI == Main.myPlayer && Main.SceneMetrics.HasHeartLantern)
        this.lifeRegen += 2;
      if (this.bleed)
        this.lifeRegenTime = 0.0f;
      float num1 = 0.0f;
      if ((double) this.lifeRegenTime >= 300.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 600.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 900.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 1200.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 1500.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 1800.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 2400.0)
        ++num1;
      if ((double) this.lifeRegenTime >= 3000.0)
        ++num1;
      if (flag)
      {
        float num2 = (this.lifeRegenTime - 3000f) / 300f;
        if ((double) num2 > 0.0)
        {
          if ((double) num2 > 30.0)
            num2 = 30f;
          num1 += num2;
        }
      }
      else if ((double) this.lifeRegenTime >= 3600.0)
      {
        ++num1;
        this.lifeRegenTime = 3600f;
      }
      if (this.sitting.isSitting || this.sleeping.isSleeping)
      {
        this.lifeRegenTime += 3f;
        num1 *= 1.3f;
      }
