      bool flag = false;
      for (int index = 0; index < 1000; ++index)
      {
        Projectile projectile = Main.projectile[index];
        if (projectile.active && projectile.sentry)
        {
          Microsoft.Xna.Framework.Rectangle hitbox = projectile.Hitbox;
