    public Vector2 CheckForGoodTeleportationSpot(
      ref bool canSpawn,
      int teleportStartX,
      int teleportRangeX,
      int teleportStartY,
      int teleportRangeY,
      Player.RandomTeleportationAttemptSettings settings)
    {
      int num1 = 0;
      int x1 = 0;
      int y1 = 0;
      int width = this.width;
      Vector2 Position = new Vector2((float) x1, (float) y1) * 16f + new Vector2((float) (-width / 2 + 8), (float) -this.height);
      while (!canSpawn && num1 < settings.attemptsBeforeGivingUp)
      {
        ++num1;
        int num2 = teleportStartX + Main.rand.Next(teleportRangeX);
        int num3 = teleportStartY + Main.rand.Next(teleportRangeY);
        int min = 5;
        int x2 = (int) MathHelper.Clamp((float) num2, (float) min, (float) (Main.maxTilesX - min));
        int y2 = (int) MathHelper.Clamp((float) num3, (float) min, (float) (Main.maxTilesY - min));
        Position = new Vector2((float) x2, (float) y2) * 16f + new Vector2((float) (-width / 2 + 8), (float) -this.height);
        if (!Collision.SolidCollision(Position, width, this.height))
        {
          if (Main.tile[x2, y2] == null)
            Main.tile[x2, y2] = new Tile();
          if ((!settings.avoidWalls || Main.tile[x2, y2].wall <= (ushort) 0) && (Main.tile[x2, y2].wall != (ushort) 87 || (double) y2 <= Main.worldSurface || NPC.downedPlantBoss) && (!Main.wallDungeon[(int) Main.tile[x2, y2].wall] || (double) y2 <= Main.worldSurface || NPC.downedBoss3))
          {
            int num4 = 0;
            while (num4 < settings.maximumFallDistanceFromOrignalPoint)
            {
              if (Main.tile[x2, y2 + num4] == null)
                Main.tile[x2, y2 + num4] = new Tile();
              Tile tile = Main.tile[x2, y2 + num4];
              Position = new Vector2((float) x2, (float) (y2 + num4)) * 16f + new Vector2((float) (-width / 2 + 8), (float) -this.height);
              Collision.SlopeCollision(Position, this.velocity, width, this.height, this.gravDir);
              if (!Collision.SolidCollision(Position, width, this.height))
                ++num4;
              else if (!tile.active() || tile.inActive() || !Main.tileSolid[(int) tile.type])
                ++num4;
              else
                break;
            }
            Position.Y -= 16f;
            int i1 = (int) Position.X / 16;
            int j1 = (int) Position.Y / 16;
            int i2 = (int) ((double) Position.X + (double) width * 0.5) / 16;
            int j2 = (int) ((double) Position.Y + (double) this.height) / 16;
            Tile tileSafely1 = Framing.GetTileSafely(i1, j1);
            Tile tileSafely2 = Framing.GetTileSafely(i2, j2);
            if (!settings.avoidAnyLiquid || tileSafely2.liquid <= (byte) 0)
            {
              if (settings.mostlySolidFloor)
              {
                Tile tileSafely3 = Framing.GetTileSafely(i2 - 1, j2);
                Tile tileSafely4 = Framing.GetTileSafely(i2 + 1, j2);
                if ((!tileSafely3.active() || tileSafely3.inActive() || !Main.tileSolid[(int) tileSafely3.type] || Main.tileSolidTop[(int) tileSafely3.type] || !tileSafely4.active() || tileSafely4.inActive() || !Main.tileSolid[(int) tileSafely4.type] ? 0 : (!Main.tileSolidTop[(int) tileSafely4.type] ? 1 : 0)) == 0)
                  continue;
              }
              if ((!settings.avoidWalls || tileSafely1.wall <= (ushort) 0) && (!settings.avoidAnyLiquid || !Collision.WetCollision(Position, width, this.height)) && (!settings.avoidLava || !Collision.LavaCollision(Position, width, this.height)) && (!settings.avoidHurtTiles || !Collision.AnyHurtingTiles(Position, width, this.height)) && !Collision.SolidCollision(Position, width, this.height) && num4 < settings.maximumFallDistanceFromOrignalPoint - 1)
              {
                Vector2 Velocity1 = Vector2.UnitX * 16f;
                if (!(Collision.TileCollision(Position - Velocity1, Velocity1, this.width, this.height, gravDir: ((int) this.gravDir)) != Velocity1))
                {
                  Vector2 Velocity2 = -Vector2.UnitX * 16f;
                  if (!(Collision.TileCollision(Position - Velocity2, Velocity2, this.width, this.height, gravDir: ((int) this.gravDir)) != Velocity2))
                  {
                    Vector2 Velocity3 = Vector2.UnitY * 16f;
                    if (!(Collision.TileCollision(Position - Velocity3, Velocity3, this.width, this.height, gravDir: ((int) this.gravDir)) != Velocity3))
                    {
                      Vector2 Velocity4 = -Vector2.UnitY * 16f;
                      if (!(Collision.TileCollision(Position - Velocity4, Velocity4, this.width, this.height, gravDir: ((int) this.gravDir)) != Velocity4))
                      {
                        canSpawn = true;
                        int num5 = y2 + num4;
                        break;
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
      return Position;
    }

